def trouver_categories(product):
    
    categories_ca = product.get("categories_ca")
    categories_code_ca = product.get("categories_code_ca")
    
    if categories_code_ca != None:
        return
    
    categories = product.get("categories") # string
    categories_tags = product.get("categories_tags") # list of string
    if categories == None and categories_tags == None:
        categories_ca = "Unclassified foods and beverages (due to missing data)"
        categories_code_ca = "9990"
        return {"category_ca":categories_ca,"category_code_ca":categories_code_ca}
    
    categories_to_check = []
    categories_fields = []

    if categories != None:
        categories_fields = categories
    elif categories_tags != None:
        categories_fields = categories_tags

    if isinstance(categories_fields, str):
        categories_to_check = [c.strip() for c in categories_fields.split(",")]
    elif isinstance(categories_fields, list):
        categories_to_check = [c.replace("-", " ").strip() for c in categories_fields]
    else:
        return {"category_ca":"Unclassified foods and beverages (due to missing data)","category_code_ca":"9990"}

    # Try to find the best fuzzy match
    nb_matches = {}
    for cat in reversed(categories_to_check):
        for keywords_list in keywords_for_categories.values(): 
            matches = get_close_matches(cat.lower(),[k.lower() for k in keywords_list], n=1, cutoff=0.8)
            if matches:
                current_category_code = filter(lambda key: matches[0] in keywords_for_categories[key], keywords_for_categories)
                codes = list(current_category_code)
                incrementation = nb_matches.get(codes[0], 0) + 1
                nb_matches[codes[0]] = incrementation
                
    if nb_matches:
            correct_code = max(nb_matches, key=lambda k: nb_matches[k])
            categories_code_ca = str(correct_code)
            categories_ca = code_and_categories_ca[categories_code_ca]
            return {"category_ca":categories_ca,"category_code_ca":categories_code_ca}
         
    return {"category_ca":"Unclassified foods and beverages (due to missing data)","category_code_ca":"9990"}

